import React from 'react'
import { Container } from 'react-bootstrap'
import styled from 'styled-components'
import ankit from './assets/images/ankit.png'
import yogi from './assets/images/yogi.png'
import anil from './assets/images/anil.jpg'


const Team = () => {
    const Teamsection=styled.section`
            height:100%;
            background:${({theme})=>theme.colors.text2};
            background-size:50%;
            padding-top:100px;
            .parent{
                height: calc(100vh - 100px);
                h1{
                    color: white;
                    font-size: 2rem;
                    font-weight: 200;
                }
                .teamcontainer{
                    display:flex;
                    flex-direction:row;
                    flex-wrap:wrap;

                    .box{
                        flex: 1 1 20%;
                    }
                }
            }
    
    `;
  return (
    <Teamsection>
        <Container>
            <div className='parent'>
                <h1>our main team </h1>
                <div className='teamcontainer'>
                    <div className='box'>
                        <div className='image_container'>
                            
                        </div>
                        <img src={ankit} alt="userprofile"/>
                        <h2>ankit yadav</h2>
                        <span>CEO</span>
                        <p>
                        A passionate QA Manager having an insatiable appetite for steering software applications & products to high quality standards. A practicing servant leader and an avid traveler.
                        </p>
                    </div>
                    <div className='box'>
                        <img src={yogi} alt="userprofile"/>
                        <h2>Yogendra Sahu</h2>
                        <span>General manager</span>
                        <p>
                        Hi this is Yogendra Sahu. As a Full Stack Developer, I work in a constantly evolving environment, due to technological advances and the strategic direction of the organization I work for. I create, maintain, audit and improve systems to meet particular needs, often as advised by a systems analyst or architect, testing software systems to diagnose and resolve. My role also covers writing diagnostic programs and designing and writing code for software to ensure efficiency. When required, I make recommendations for future developments.
                        </p>
                    </div>
                    <div className='box'>
                        <img src={ankit} alt="userprofile"/>
                        <h2>anil kapse</h2>
                        <span>General manager</span>
                        <p>
                        Hi, I'm Anil Kapse, as a Senior UI developer, my job is essentially to turn designs/business idea into reality via code and enable user to interact with a data in a safe and user-friendly way.
                        </p>
                    </div>

                </div>
            </div>
        </Container>
    </Teamsection>
  )
}

export default Team
